import React from 'react'
import {FcLike,FcLikePlaceholder} from "react-icons/fc";
import { useState } from 'react';
import { ToastContainer,toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Card(props) {

    let course=props.course;
    const [likeicon,setLikeIcon]=useState(false);

    function clickHandler(){
      setLikeIcon(!likeicon);
      toast.success('j');
    }

  return (
    <div className="">
    <div className='relative'>
    <img src={course.image.url} alt="myimg" className="relative"></img>
    <div className='w-[20px] h-[20px] absolute bottom-0 right-0 rounded-full bg-white flrx align-center'>
    <button onClick={clickHandler}>
    {
      likeicon===false ? <FcLikePlaceholder/>:<FcLike/>
    }
    </button>
    </div>
    </div>
    

    <div>
    <p className="content  text-sm   font-semibold">{course.title}</p>
    <p className="text-xs">{
      course.description
      }</p>
    </div>
    </div>
  )
}

export default Card
