import React from 'react';

function card(props) {
   let info=props.info;

  return (
    <div className='w-[40%] mx-auto bg-slate-300 relative m-4'>
    
        <div className=' w-[60px] h-[60px] rounded-full bg-[blue] 
        relative absolut top-[-30px] m-0'>
            <img src={info.image} alt={info.id} width="57px" height="40px" 
            className='rounded-full absolute bottom-0 left-0'></img>
        </div>
       <div className=''>
       <div >
            {info.name}
        </div>
        <div>
            {info.job}
        </div>
        <div className='text-sm'>
            {info.text}
        </div>
       </div>
    </div>
  )
}

export default card
