import React from "react";

function Filter(props){
   
    let catagory=props.catagory;
    let filterData=props.filterData; 
    let setCatagory=props.setCatagory;
   // val.map((value) => console.log(value))
    //console.log(val);

    function btnClick(title){
        setCatagory(title);
    }

    return (
        <div className="flex justify-center align-center  my-4 flex-wrap  ">  
        {
            filterData.map((data)=> (
               <button key={data.id}  className={`bg-bgDark hover:border-1px solid white text-white   py-0.5 px-1 rounded mx-1 font-thin text-xs
               hover:scale-[1.1] ease-in duration-200 border-2
               ${catagory==data.title?"bg-opacity-60 border-white":"bg-opacity-40 border-transparent"}
               `}
               onClick={()=>btnClick(data.title)}>
                {data.title}
                </button>
            )
             )
        }
        </div>
    )
}

export default Filter;
