import Card from "./Card";
import { toast } from "react-toastify";


function Cards(props){

   //alert("stop");
    let courses=props.courses;
    let catagory=props.catagory;
    let setCatagory=props.setCatagory;

    //console.log( courses);
   // console.log(courses);
 function getCourses() {

  if(catagory=="All"){
    let allCourses=[];
    Object.values(courses).forEach(array => {
     // console.log(array)
    Object.values(array).forEach(course=>{
        // console.log(course);
         allCourses.push(course);
       })   
    })
         return allCourses;
  }
  else{
     return courses[catagory];
   }
 }

  let allCourses=getCourses();

 //console.log("All courses are" +allCourses);

    return(
        <div className=" flex flex-wrap gap-y-10 justify-center align-center">
            {
               allCourses.map( (course)=>
                <div className="w-[30%] mx-2 bg-white">
                <Card key={course.id} course={course} />
                </div>
               )
            }
        </div>
    )
}

export default Cards;
