import React from 'react';

function card(props) {
   let info=props.info;

  return (

    <div className='w-[40%] min-w-[0px] mx-auto bg-slate-300 relative p-2 flex flex-col flex-wrap justify-center align-center'>
    <img src={info.image} alt={info.id} width="57px" height="40px" 
            className='rounded-full absolute top-[-30px]  mt-4'></img>

       <div className='mt-5 flex justify-center '>
            {info.name}
        </div>
        <div>
            {info.job}
        </div>
        <div className='text-sm'>
            {info.text}
        </div>
       </div>
  )
}

export default card
