import React from "react";
import {apiUrl,filterData} from "./data.js";
import Filter from "./components/Filter.js";
import Cards from "./components/Cards.js";
import { useState,useEffect } from "react";
import Spinner from "./components/Spinner.js";




const App = () => {

  const [courses,setCourses]=useState({});
  const [loading,setLoading]=useState(true);
  const [catagory,setCatagory]=useState(filterData[0].title);

    async function getApiData(){
            setLoading(true);
        try{
            let response=await fetch(apiUrl);
            let output=await response.json();
            setCourses(output.data);
        }
        catch(error){
            console.log("Error in fetching cards data");
        }
        setLoading(false);
    }

 useEffect(()=>{
    getApiData();
 }, [])

  return (
    <div className="min-h-screen flex flex-col bg-bgDark2">
  <h2 className="text-white text-center font-medium bg-bgDark ">Top Courses</h2>
    <div className="w-11/12 mx-auto max-w-[1200px]">
    <Filter filterData={filterData}>
      
    </Filter>
  <div className="w-11/12 mx-auto">
      {
        loading ? (
                  <Spinner/>
                 ):(
                    <Cards  courses={courses}/>
                )
      }
  </div>
    </div>
    </div>
  )
};

export default App;
