import React from 'react';

function card(props) {
   let info=props.info;

  return (
    <div className='w-[40%] mx-auto bg-slate-300'>
        <div className=' w-[60px] h-[60px] rounded-full bg-[blue] flex justify-center relative absolut '>
            <img src={info.image} alt={info.id} width="57px" height="40px" className='rounded-full absolute bottom-0 left-0'></img>
        </div>
       <div className='flex justify-center align center flex-col'>
       <div>
            {info.name};
        </div>
        <div>
            {info.job}
        </div>
        <div>
            {info.text}
        </div>
       </div>
    </div>
  )
}

export default card
