import React from 'react';

function card(props) {
   let info=props.info;

  return (
    <div>
    <div className='w-[40%] mx-auto bg-slate-300 relative p-2 flex flex-col justify-center align-center'>
    <img src={info.image} alt={info.id} width="57px" height="40px" 
            className='rounded-full absolute top-[-30px]  mt-4'></img>
       <div >
            {info.name}
        </div>
        <div>
            {info.job}
        </div>
        <div className='text-sm'>
            {info.text}
        </div>
       </div>

    </div>
  )
}

export default card
