import React from 'react'
import {FcLike,FcLikePlaceholder} from "react-icons/fc";
import { useState } from 'react';
import {toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Card(props) {
  toast.success('JJJJJJ');

    let course=props.course;
    const [likeicon,setLikeIcon]=useState(false);

    function clickHandler(){
      setLikeIcon(!likeicon);
      toast.success('JJJJJJ');
      console.log("success");
    }

  return (
    <div className="bg-bgDark h-full">
    <div className='relative'>
    <img src={course.image.url} alt="myimg" className="relative"></img>
    <div className='w-[20px] h-[20px] absolute bottom-0 right-0 rounded-full bg-white flex justify-center align-center'>
    <button onClick={clickHandler}>
    {
      likeicon===false ? <FcLikePlaceholder/>:<FcLike/>
    }
    </button>
    </div>
    </div>
    

    <div className='text-white'>
    <p className="content  text-sm leading-6  font-semibold">{course.title}</p>
    <p className="text-xs mt-2">{

      (course.description.substr(0,100)+"...")
   
      }</p>
    </div>
    </div>
  )
}

export default Card
