import React from 'react';

function card(props) {
   let info=props.info;

  return (

    <div className='w-1/5 mt-3 min-w-[250px] bg-white mx-auto  relative p-2 flex flex-col flex-wrap justify-center align-center'>
    <img src={info.image} alt={info.id} width="57px" height="40px" 
            className='rounded-full absolute top-[-30px]  mt-4'></img>

       <div className='mt-5 flex justify-center font-bold text-2xl'>
            {info.name}
        </div>
        <div className='flex justify-center   text-violet-300 uppercase text-sm font-medium '>
            {info.job}
        </div>
        <div className='text-sm mt-4 txt-center text-slate-500'>
            {info.text}
        </div>
       <div className='flex justify-center'>
       <button className='p-3'>  - </button>
        <button className='p-3'> + </button>
       </div>

       <div className='flex justify-center'>
        <button className='bg-violet-400 hover:bg-violet-500 px-3 py-1 font- bold text-lg rounded'>
            Surprise Me
        </button>
       </div>

       </div>
  )
}

export default card
