import React from 'react';
import { useState } from 'react';
import {FaQuoteRight,FaQuoteLeft} from 'react-icons/fa'



function Card(props) {
   let info=props.info;


  return (

    <div>
    <img src={info.image} alt={info.id} width="57px" height="40px" 
            className='rounded-full absolute top-[-30px] '></img>

       <div className='font-bold text-xl'>
            {info.name}
        </div>
        <div className='  text-violet-300 uppercase text-xs font-medium '>
            {info.job}
        </div>
       <div className='w-[15px] h-[15px] text-violet-300 mx-auto my-1'>
       <FaQuoteLeft/>
       </div>
        <div className='text-xs mt-1 txt-center text-slate-500'>
            {info.text}
        </div>
        <div className='w-[15px] h-[15px] text-violet-300 mx-auto my-1'>
        <FaQuoteRight/>
        </div>

       </div>
  )
}

export default Card
