import React from 'react'
import {FcLike,FcLikePlaceholder} from "react-icons/fc";
import { useState } from 'react';

function Card(props) {

    let course=props.course;
    const [likeicon,setLikeIcon]=useState(false);

  return (
    <div className="">
    <div className='relative'>
    <img src={course.image.url} alt="{course.image.alt}" className="relative"></img>
    <div className='absolute bottom-0 right-0'>
    <button>
    likeicon==false?<FcLikePlaceholder/>:<FcLike/>>;
    </button>
    </div>
    </div>
    

    <div>
    <p className="content  text-sm   font-semibold">{course.title}</p>
    <p className="text-xs">{
      course.description
      }</p>
    </div>


    </div>
  )
}

export default Card
