import React from 'react';
import { useState } from 'react';
import {FaQuoteRight,FaQuoteLeft} from 'react-icons/fa'
import {AiFillLeftCircle,AiFillRightCircle} from 'react-icons/ai'


function Card(props) {
   let info=props.info;
   const [curr,setCurr]=useState(0);
   let size=info.size;

   function clickHandlerLeft(){
        setCurr((curr+1))
   }

   function clickHandlerRight(){

   }

  return (

    <div className='w-[50%] min-w-[200px] h-[30%] bg-white mx-auto  p-2 flex flex-col flex-wrap justify-center text-center relative mb-5'>
    <img src={info.image} alt={info.id} width="57px" height="40px" 
            className='rounded-full absolute top-[-30px] '></img>

       <div className='font-bold text-xl'>
            {info.name}
        </div>
        <div className='  text-violet-300 uppercase text-xs font-medium '>
            {info.job}
        </div>
       <div className='w-[15px] h-[15px] text-violet-300 mx-auto my-1'>
       <FaQuoteLeft/>
       </div>
        <div className='text-xs mt-1 txt-center text-slate-500'>
            {info.text}
        </div>
        <div className='w-[15px] h-[15px] text-violet-300 mx-auto my-1'>
        <FaQuoteRight/>
        </div>
       <div className=''>
       <button className='p-2 text-violet-400' onClick={clickHandlerLeft}>   <AiFillLeftCircle/> </button>
        <button className='p-3 text-violet-400' onClick={clickHandlerRight}>  <AiFillRightCircle/> </button>
       </div>

       <div className=''>
        <button className='bg-violet-400 hover:bg-violet-500 px-3   text-sm rounded'>
       Surprise
        </button>
       </div>

       </div>
  )
}

export default Card
